        '{"data":{"copra":{"childImageSharp":{"fluid":{"base64":"data:image/png;base64,","aspectRatio":1,"src":"/static/44fdaceb6a5dd82e686f862c6a7bd434/ddb9e/bill-copra.png","srcSet":"/static/44fdaceb6a5dd82e686f862c6a7bd434/59beb/bill-copra.png 200w,\\n/static/44fdaceb6a5dd82e686f862c6a7bd434/c0bbd/bill-copra.png 400w,\\n/static/44fdaceb6a5dd82e686f862c6a7bd434/ddb9e/bill-copra.png 500w","sizes":"(max-width: 500px) 100vw, 500px"}}},"smart":{"childImageSharp":{"fluid":{"base64":"data:image/png;base64,","aspectRatio":1,"src":"/static/d30ab81b486b9cd5d9c722b24771d8a4/ddb9e/bill-smart.png","srcSet":"/static/d30ab81b486b9cd5d9c722b24771d8a4/59beb/bill-smart.png 200w,\\n/static/d30ab81b486b9cd5d9c722b24771d8a4/c0bbd/bill-smart.png 400w,\\n/static/d30ab81b486b9cd5d9c722b24771d8a4/ddb9e/bill-smart.png 500w","sizes":"(max-width: 500px) 100vw, 500px"}}},"opa":{"childImageSharp":{"fluid":{"base64":"data:image/png;base64,","aspectRatio":1,"src":"/static/81ba59be1cdefc1a45fdbbdf0a900e6d/ddb9e/bill-opa.png","srcSet":"/static/81ba59be1cdefc1a45fdbbdf0a900e6d/59beb/bill-opa.png 200w,\\n/static/81ba59be1cdefc1a45fdbbdf0a900e6d/c0bbd/bill-opa.png 400w,\\n/static/81ba59be1cdefc1a45fdbbdf0a900e6d/ddb9e/bill-opa.png 500w","sizes":"(max-width: 500px) 100vw, 500px"}}}}}'
